        Category category;
        bytes uri;
    }

    struct Order {
        uint24 quantity;
        uint32 date;
        uint offerPrice;
        uint commitment;
        address customer;
        Stage stage;
        Info info;
        address firstSigner;
    }

    struct Info {
        bytes destination;
        bytes contacts;
    }

    // -------> PARAMS <-------

    struct AddItemParam {
        uint8 quantity;
        uint8 categorySelector;
        uint priceLimit;
        string imageUrI;
        string description;
        string location;
        address seller;
    }

    function listNewItem(AddItemParam memory) external returns(Item memory) ;
    function placeOrder(Order memory order, uint itemId, uint24 newQuantity) external returns(bool);
    function getItem(uint itemId) external view returns(Item memory);
